void main() { print(getMostExpensiveSoftware()); } Map<String, dynamic> getMostExpensiveSoftware() { return jobs.reduce((a, b) => convertSalaryIntoUsd(b['salary'], b['currency']) > convertSalaryIntoUsd(a['salary'], a['currency']) ? b : a); } double convertSalaryIntoUsd(double salary, String currency) { return currency == "USD" ? salary : salary / currencies[currency]; } final List<Map<String, dynamic>> jobs = [ {"job": "software", "salary": 150000, "currency": "PKR"}, {"job": "chef", "salary": 4000, "currency": "INR"}, {"job": "cleaner", "salary": 400, "currency": "USD"}, {"job": "xyz", "salary": 40, "currency": "EURO"}, ]; final Map<String, double> currencies = { "PKR": 1, "INR": 3, "USD": 278.50, "EURO": 300, };
